import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { LoginUsuario } from 'src/app/model/login-usuario';
import { AuthService } from 'src/app/service/auth.service';
import { TokenService } from 'src/app/service/token.service';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { BrowserModule } from '@angular/platform-browser';

imports: [
  BrowserModule,
  FormsModule,
  ReactiveFormsModule,
]

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent implements OnInit{
isLogged = false;
isLogginFail = false;
loginUsuario! : LoginUsuario;
nombreUsuario! : string;
password! : string;
roles: string[] = [];
errMsj!: string;

constructor(private tokenService: TokenService, private authService : AuthService, private router: Router){
  
}


  ngOnInit(): void {
    if(this.tokenService.getToken()){
      this.isLogged = true;
      this.isLogginFail = false;
      this.roles = this.tokenService.getAuthorities();
    }
  }

  onLogin(): void{
    this.loginUsuario = new LoginUsuario(this.nombreUsuario, this.password);
     this.authService.login(this.loginUsuario).subscribe(data => {
        this.isLogged = true;
        this.isLogginFail = false;
        this.tokenService.setToken(data.token);
        this.tokenService.setUsername(data.nombreUsuario);
        this.tokenService.setAuthorities(data.authorities);
        this.roles = data.authorities;
        this.router.navigate([''])
      }, err =>{
        this.isLogged = false;
        this.isLogginFail = true;
        this.errMsj = err.error.mensaje;
        console.log(this.errMsj);
      }

      )
  }
}
<section class="vh-100" style="background-color:  #a1b5cb">
    <div class="container py-5 h-100">
      <div class="row d-flex justify-content-center align-items-center h-100">
        <div class="col-12 col-md-8 col-lg-6 col-xl-5">
          <div class="card shadow-2-strong" style="border-radius: 1rem;">
            <div class="card-body p-5 text-center">
  
              <h3 class="mb-5">Sign in</h3>

              <form #f="ngForm" (ngSubmit)="onLogin()" novalidate >
                <div class="form-outline mb-4">
                  <input type="email" id="nombreUsuario" name="nombreUsuario" class="form-control form-control-lg" [(ngModel) ]="nombreUsuario" required/>
                  <label class="form-label" for="typeEmailX-2">Usuario</label>
                </div>
    
                <div class="form-outline mb-4">
                  <input type="password" id="password" class="form-control form-control-lg" [(ngModel) ]="password" required />
                  <label class="form-label" for="typePasswordX-2">Contraseña</label>
                </div>
    
                <!-- Checkbox -->
                <!--<div class="form-check d-flex justify-content-start mb-4">
                  <input class="form-check-input" type="checkbox" value="" id="form1Example3" />
                  <label class="form-check-label" for="form1Example3"> Recordar Contraseña </label>
                </div>-->
    
                <button class="btn btn-primary btn-lg btn-block" type="submit">Login</button>
                </form>    
  

              <hr class="my-4">
  
              <button class="btn btn-lg btn-block btn-primary" style="background-color: #aa3bde;"
                type="submit"><i class="fab fa-google me-2"></i> Sign in with google</button>
              <button class="btn btn-lg btn-block btn-primary mb-2" style="background-color: #3b5998;"
                type="submit"><i class="fab fa-facebook-f me-2"></i>Sign in with facebook</button>
  
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
